@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'crud-application';
}

<div class="card  loginbox" style="width: 40rem;">
  <div class="card-body">
    <h5 class="card-title logintitle">ArguSoft</h5>
    <H4 class="card-text logindesc">WELCOME TO STAFFS-SPHERE </H4>
    <a href="#" class="btn btn-success loginbutton">Login/Signup</a>
  </div>
</div>

